from shiny import *
xdim=75
ydim=40
steps=200
def create_grid(xdim,ydim):return( [[x]*xdim for x in [0]* ydim] )

def neigbors(grid):
  neib = create_grid(xdim,ydim)
  for i in range(ydim):
    for j in range(xdim):
      for y in range(i-1,i+2):
        for x in range(j-1,j+2):
          neib[i][j] += grid[y%ydim][x%xdim]
      neib[i][j] = neib[i][j]-grid[i][j]
  return(neib)

def step(grid):
  neibs = neigbors(grid)
  for i in range(ydim):
    for j in range(xdim):
      if grid[i][j] == 1:
        if neibs[i][j] in [2,3]:
          grid[i][j] = 1
        else:
          grid[i][j] = 0
      else:
        if neibs[i][j] == 3:
          grid[i][j] = 1
  return(grid)

def populate(grid,coord_array):
    coord_range = [(y, x) for x in range(ydim) for y in range(xdim)]
    for (y,x) in coord_range:
        if (y,x) in coord_array:
            grid[y][x]=1
    return(grid)

def Vis(grid):
    vis = str()
    for y in range(ydim):
        for x in range(xdim):
            if grid[y][x] == 0: vis += '░'
            else: vis += '█'
        vis += '\n'
    return vis

